function partOne($input){
    $result = [];
    $total = 0;
    
    $stringSplit = str_split($input);
    foreach($stringSplit as $s){
        if(count($result) == 4){
            if($result[0] == $result[1] || $result[0] == $result[2] || $result[0] == $result[3] ||
                $result[1] == $result[2] || $result[1] == $result[3] || $result[2] == $result[3]){
                    //means there is a double
                    array_shift($result);
                    $result[] = $s;
            } else { break; }
        } else { $result[] = $s; }
        $total++;
    }

    echo '<br/>Part 1: ' . $total;
}

